contracts/Basecamp.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

/**
 * A smart contract for the The Graph Builders Basecamp challenges
 * @author Kevin Jones
 */
contract Basecamp is
	Initializable,
	OwnableUpgradeable,
	ERC1155Upgradeable,
	AccessControlUpgradeable
{
	string public name;
	string public symbol;

	struct Credential {
		bool enabled;
		string name;
		string url;
	}

	mapping(uint8 => Credential) public credentials;

	bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
	bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

	event CredentialSet(bool enabled, uint8 id, string name, string url);
	event CredentialMinted(address to, uint8 id);
	event AdminAdded(address admin);
	event MinterAdded(address minter);
	event AdminRemoved(address admin);
	event MinterRemoved(address minter);
	event Withdraw(uint256 amount);

	/// @custom:oz-upgrades-unsafe-allow constructor
	constructor() {
		_disableInitializers();
	}

	function initialize(address _owner, address _minter) public initializer {
		__Ownable_init(_owner);
		__ERC1155_init("http://example.com/");
		__AccessControl_init();

		name = "Basecamp";
		symbol = "CRED";

		_setRoleAdmin(MINTER_ROLE, ADMIN_ROLE);
		_grantRole(ADMIN_ROLE, _owner);
		_grantRole(MINTER_ROLE, _minter);
	}

	/**
	 * Add a credential with URL
	 * @param _enabled Whether the credential is enabled
	 * @param _id The ID of the credential
	 * @param _name The name of the credential
	 * @param _url The URL for the credential's metadata
	 */
	function setCredential(
		bool _enabled,
		uint8 _id,
		string memory _name,
		string memory _url
	) public onlyRole(ADMIN_ROLE) {
		credentials[_id] = Credential(_enabled, _name, _url);
		emit CredentialSet(_enabled, _id, _name, _url);
	}

	/**
	 * Mint a credential
	 * @param id The ID of the credential
	 * @param _account The account to mint the credential to
	 */
	function mint(uint8 id, address _account) public onlyRole(MINTER_ROLE) {
		require(credentials[id].enabled, "credential not enabled");
		_mint(_account, id, 1, "0x00");
		emit CredentialMinted(_account, id);
	}

	/**
	 * Add a minter
	 * @param _account The account to add as a minter
	 */
	function addMinter(address _account) public onlyRole(ADMIN_ROLE) {
		_grantRole(MINTER_ROLE, _account);
		emit MinterAdded(_account);
	}

	/**
	 * Add an admin
	 * @param _account The account to add as an admin
	 */
	function addAdmin(address _account) public onlyRole(ADMIN_ROLE) {
		_grantRole(ADMIN_ROLE, _account);
		emit AdminAdded(_account);
	}

	/**
	 * Remove an admin
	 * @param _account The account to remove as an admin
	 */
	function removeAdmin(address _account) public onlyRole(ADMIN_ROLE) {
		_revokeRole(ADMIN_ROLE, _account);
		emit AdminRemoved(_account);
	}

	/**
	 * Remove a minter
	 * @param _account The account to remove as a minter
	 */
	function removeMinter(address _account) public onlyRole(ADMIN_ROLE) {
		_revokeRole(MINTER_ROLE, _account);
		emit MinterRemoved(_account);
	}

	/**
	 * Function that allows the owner to withdraw ETH from the contract
	 */
	function withdraw() public onlyOwner {
		uint256 balance = address(this).balance;
		(bool success, ) = payable(owner()).call{ value: balance }("");
		require(success, "Transfer failed");
		emit Withdraw(balance);
	}

	/**
	 * Function that allows the contract to receive ETH
	 */
	receive() external payable {}

	/**
	 * Override the URI function to return different URLs for each token
	 * @param id The token ID
	 */
	function uri(
		uint256 id
	) public view virtual override returns (string memory) {
		return credentials[uint8(id)].url;
	}

	function supportsInterface(
		bytes4 interfaceId
	)
		public
		view
		virtual
		override(ERC1155Upgradeable, AccessControlUpgradeable)
		returns (bool)
	{
		return super.supportsInterface(interfaceId);
	}

	/**
	 * Override safeTransferFrom to revert
	 */
	function safeTransferFrom(
		address from,
		address to,
		uint256 id,
		uint256 amount,
		bytes memory data
	) public override(ERC1155Upgradeable) {
		revert("Token soulbound!");
	}

	/**
	 * Override safeBatchTransferFrom to revert
	 */
	function safeBatchTransferFrom(
		address from,
		address to,
		uint256[] memory ids,
		uint256[] memory amounts,
		bytes memory data
	) public override(ERC1155Upgradeable) {
		revert("Token soulbound!");
	}
}

contracts/Validator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import { Strings } from "@openzeppelin/contracts/utils/Strings.sol";
import { FunctionsClient } from "./@chainlink/contracts/src/v0.8/functions/dev/1_0_0/FunctionsClient.sol";
import { FunctionsRequest } from "./@chainlink/contracts/src/v0.8/functions/dev/1_0_0/libraries/FunctionsRequest.sol";
import { Basecamp } from "./Basecamp.sol";

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract Validator is
    FunctionsClient,
    OwnableUpgradeable,
    AccessControlUpgradeable
{
    using FunctionsRequest for FunctionsRequest.Request;

    error AlreadyMinted();
    error InvalidSourceCode();
    error ValidationFailed();
    error TransferFailed();

    bytes32 private immutable ADMIN_ROLE_HASH;

    struct MissionRequest {
        address account;
        uint8 missionIndex;
        bool isProcessed;
    }

    Basecamp public basecamp;
    bytes32 public donId;

    mapping(bytes32 => MissionRequest) private requests;
    mapping(uint8 => mapping(address => bool)) public accountMinted;
    mapping(uint8 => bytes32) public missionCodeHashes;

    event BasecampAddressSet(address indexed newBasecampAddress);
    event DonIdSet(bytes32 indexed newDonId);
    event MissionSubmitted(
        bytes32 indexed requestId,
        uint8 indexed missionIndex,
        string queryUrl,
        address indexed account
    );
    event MissionValidated(
        bytes32 indexed requestId,
        uint8 indexed missionIndex,
        uint256 isValid,
        bool success,
        address indexed account
    );
    event Withdraw(uint256 amount);

    constructor(
        address _functionsRouterAddress
    ) FunctionsClient(_functionsRouterAddress) {
        ADMIN_ROLE_HASH = keccak256("ADMIN_ROLE");
    }

    function initialize(
        address _owner,
        address _basecampAddress,
        bytes32 _donId
    ) external initializer {
        __Ownable_init(_owner);
        __AccessControl_init();

        _grantRole(ADMIN_ROLE_HASH, _owner);
        basecamp = Basecamp(payable(_basecampAddress));
        donId = _donId;

        emit BasecampAddressSet(_basecampAddress);
        emit DonIdSet(donId);
    }

    function setMissionCodeHash(
        uint8 missionIndex,
        string calldata javaScriptSourceCode
    ) external onlyRole(ADMIN_ROLE_HASH) {
        missionCodeHashes[missionIndex] = keccak256(bytes(javaScriptSourceCode));
    }

    function setBasecampAddress(
        address _basecampAddress
    ) external onlyRole(ADMIN_ROLE_HASH) {
        basecamp = Basecamp(payable(_basecampAddress));
        emit BasecampAddressSet(_basecampAddress);
    }

    function setDonId(bytes32 _donId) external onlyRole(ADMIN_ROLE_HASH) {
        donId = _donId;
        emit DonIdSet(_donId);
    }

    function validateMission(
        uint8 missionIndex,
        string calldata javaScriptSourceCode,
        uint64 subscriptionId,
        uint32 gasLimit,
        string calldata queryUrl
    ) external returns (bytes32) {
        if(accountMinted[missionIndex][msg.sender]) {
            revert AlreadyMinted();
        }

        bytes32 submittedCodeHash = keccak256(bytes(javaScriptSourceCode));
        if(submittedCodeHash != missionCodeHashes[missionIndex]) {
            revert InvalidSourceCode();
        }

        FunctionsRequest.Request memory req;
        req.initializeRequestForInlineJavaScript(javaScriptSourceCode);

        string[] memory args = new string[](2);
        args[0] = Strings.toHexString(msg.sender);
        args[1] = queryUrl;
        req.setArgs(args);

        bytes32 requestId = _sendRequest(
            req.encodeCBOR(),
            subscriptionId,
            gasLimit,
            donId
        );

        requests[requestId] = MissionRequest({
            account: msg.sender,
            missionIndex: missionIndex,
            isProcessed: false
        });

        emit MissionSubmitted(requestId, missionIndex, queryUrl, msg.sender);
        return requestId;
    }

    function fulfillRequest(
        bytes32 requestId,
        bytes memory response,
        bytes memory err
    ) internal override {
        if (err.length > 0) {
            revert(string(err));
        }


        MissionRequest storage request = requests[requestId];
        if(request.isProcessed) {
            return;
        }

        uint256 isValid = abi.decode(response, (uint256));

        unchecked {
            if (isValid == 1) {
                request.isProcessed = true;
                accountMinted[request.missionIndex][request.account] = true;

                basecamp.mint(request.missionIndex, request.account);

                emit MissionValidated(
                    requestId,
                    request.missionIndex,
                    1,
                    true,
                    request.account
                );
                return;
            }
        }

        emit MissionValidated(
            requestId,
            request.missionIndex,
            0,
            false,
            request.account
        );
        revert ValidationFailed();
    }

    function withdraw() external onlyOwner {
        uint256 balance = address(this).balance;
        (bool success, ) = payable(owner()).call{value: balance}("");
        if(!success) revert TransferFailed();
        emit Withdraw(balance);
    }

    receive() external payable {}
}
